import React, { Component} from 'react';
import "./InputTodo.scss"

class Form extends Component {
    state={
        title: "",
        correctLength: true,
        colorLength: true
    };
    onChangeHandler = (e) => {
        this.setState({title:e.target.value})
        e.target.value.length <= 3 || e.target.value.length > 25 ? this.setState({colorLength: true}) : this.setState({colorLength: false})
    }
    onUpdateHandler = (e) => {
        e.preventDefault();
        if(this.state.title.length >3 && this.state.title.length < 25 ) {
            this.props.addTodo(this.state);
            this.setState({
                title: "",
                correctLength: true
            });
        } else { 
            this.setState({
                correctLength: false
            });
        }
    }
    render() {
        return (
            <form className = "form-container" onSubmit = {this.onUpdateHandler}>
                <input 
                className= "input-text"
                type="text" 
                placeholder="ToDo" 
                value={this.state.title} 
                onChange={ this.onChangeHandler} 
                onInput={(e)=>{

                }} />
                <input 
                type="submit" 
                value="Submit"
                className= "input-submit"
                />
                {this.state.colorLength ? <input style={{color: "red"}}></input> : style={color: "green"}}
                {this.state.correctLength ? null : <p>"Text muss zwischen 3-25 zeichen haben"</p>}
            </form>

        );
    }
}

export default Form;
