import React from "react"
import "./TodoItem.scss";
import Priority from "../Priority"

const lowStyle = {
    opacity: "0.3"
}
const highStyle = {
    fontWeight: "bold"
}
class TodoItem extends React.Component {
    state = {
        priority: ""
    }

    onChangeHandler = (e) => {
        console.log(e.target.value)
        if (e.target.value === "Low") {
            this.setState({
                priority: "Low"
            })
        } else if (e.target.value === "Medium") {
            this.setState({
                priority: "Medium"
            })
        } else if (e.target.value === "High") {
            this.setState({
                priority: "High"
            })
        }
        console.log(this.state)
    }
    componentWillUnmount() {
    }
    render() {
        return (
            <li className="todo-item">
                <input
                    type="checkbox"
                    checked={this.props.completed}
                    onChange={() => this.props.handleChange(this.props._id)}>
                </input>
                {this.state.priority === "High" ? <img 
                src={require("../../img/images.png")}
                style={ {width:"20px", display:"inline-block"}}
                alt={"alt"}
            /> : null}
                <span
                    className={this.props.completed ? "completedStyle" : null}
                    style={(this.state.priority !== "Medium" && this.state.priority !== "High" && this.state.priority !== "") ? lowStyle 
                    : (this.state.priority !== "Medium" && this.state.priority !== "Low" && this.state.priority !== "") ? highStyle : null}
                >
                    {this.props.title}
                </span>
                <Priority onChangeHandler={this.onChangeHandler} />
                <button onClick={() => this.props.deleteTodoHandler(this.props._id)}>Delete</button>
            </li>
        )
    }

}

export default TodoItem



