import React from 'react';

function Form() {
                return (
                    <div>


        <form onSubmit={this.handleSubmit}>
          Kommentar
          <label>
            <textarea id="textarea" value={this.state.comment} onChange={this.handleTextarea} />
          </label>
          Name * 
          <label>
            <input id="name" type="text" value={this.state.name} onChange={this.handleInputName} />
          </label>
          E-mail * 
          <label>
            <input id="email" type="email" value={this.state.email} onChange={this.handleInputEmail} />
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );

}

export default Form;
