import React, { Component, useState } from 'react';
import "./InputTodo.scss"

const Form = (props) => {

    const [title, setTitle] = useState("");
    const [correctLength, setCorrectLength] = useState(true)
    const [colorLength, setColorLength] = useState(true)

    const onChangeHandler = (e) => {
        this.setState({title:e.target.value})
        e.target.value.length <= 3 || e.target.value.length > 25 ? setColorLength(true) :  setColorLength(false)
        setTitle(e.target.value)
    }

    const onUpdateHandler = (e) => {
        e.preventDefault();
        if(title.length >3 && title.length < 25 ) {
            props.addTodo(title);
            setTitle("")
            setCorrectLength(true)
        } else { 
            setCorrectLength(false);
        }


        return (
            <>
                <form action="" onSubmit={onSubmitHandler} className="form-container">
                    <input
                        style={ colorLength ? {color: "red"} : {color: "green"} }
                        className="input-text"
                        type="text"
                        placeholder="ToDo..."
                        value={title}
                        onChange={onChangeHandler}
                    />
                    <input type="submit" value="hinzufÃ¼gen" className="input-submit" />
                </form>
                {correctLength ?
                    null : 
                    <p>Dein ToDo muss zwischen 3 und 25 Zeichen lang sein!</p>
                }
            </>
        );


}
}
export default Form;


