import React, { Component, useState } from 'react';
import "./InputTodo.scss"

const Form = (props) => {

    const [title, setTitle] = useState("");
    const [correctLength, setCorrectLength] = useState(true)
    const [colorLength, setColorLength] = useState(true)

    const onChangeHandler = (e) => {
        this.setState({title:e.target.value})
        e.target.value.length <= 3 || e.target.value.length > 25 ? this.setState({colorLength: true}) : this.setState({colorLength: false})
        setTitle(e.target.value)
    }

    const onUpdateHandler = (e) => {
        e.preventDefault();
        if(title.length >3 && title.length < 25 ) {
            props.addTodo(title);
            setTitle("")
            setCorrectLength(true)
        } else { 
            setCorrectLength(false);
        }

    return (
        <form className = "form-container" onSubmit = {onChangeHandler}>
            <input 
            style={ colorLength ? {color: "red"} : {color: "green"} }
            className= "input-text not-correct"
            type="text" 
            placeholder="ToDo" 
            value={title} 
            onChange={onChangeHandler} 
            /> 
            <input 
            type="submit" 
            value="Submit"
            className= "input-submit"
            />
            {correctLength ? null : <p>"Text muss zwischen 3-25 zeichen haben"</p>}
        </form>

    );
}
}
export default Form;


