import React, { useState } from "react"
import "./TodoItem.scss";
import Priority from "../Priority"

const lowStyle = {
    opacity: "0.3"
}
const highStyle = {
    fontWeight: "bold"
}

const TodoItem = (props) => {
    const [priority, setPriority] = useState("")

    const onChangeHandler = (e) => {
        setPriority(e.target.value)
    }
    return (
        <li className="todo-item">
            <input
                type="checkbox"
                checked={props.completed}
                onChange={() => props.handleChange(props._id)}>
            </input>
            {priority === "High" ? <img 
            src={require("../../img/images.png")}
            style={ {width:"20px", display:"inline-block"}}
            alt={"alt"}
        /> : null}
            <span
                className={props.completed ? "completedStyle" : null}
                style={(priority !== "Medium" && priority !== "High" && priority !== "") ? lowStyle 
                : (priority !== "Medium" && priority !== "Low" && priority !== "") ? highStyle : null}
            >
                {props.title}
            </span>
            <Priority onChangeHandler={onChangeHandler} />
            <button onClick={() => props.deleteTodoHandler(props._id)}>Delete</button>
        </li>
    )
}
