import "./InputTodo.scss"

const Form = (props) => {

    const [title, setTitle] = useState("");
    const [correctLength, setCorrectLength] = useState(true)
    const [colorLength, setColorLength] = useState(true)


    return (
        <form className = "form-container" onSubmit = {setTitle(e.target.value)}>
            <input 
            style={ this.state.colorLength ? {color: "red"} : {color: "green"} }
            className= "input-text not-correct"
            type="text" 
            placeholder="ToDo" 
            value={this.state.title} 
            onChange={ this.onChangeHandler} 
            /> 
            <input 
            type="submit" 
            value="Submit"
            className= "input-submit"
            />
            {this.state.correctLength ? null : <p>"Text muss zwischen 3-25 zeichen haben"</p>}
        </form>

    );
}


